 const updateUp =()=> setPostId(prev=>prev+1)
 const updateDown =()=> setPostId(prev=>prev+1)


   
  fetch('https://jsonplaceholder.typicode.com/posts')
 .then(res => res.json())
 .then(data => setPosts(data))
 
  return (
  <>
  <div>
    
  </div>
      <h3>{posts?.map( (post) => (
        <>{post.id === postId
            ?(<>{post.title}</>)
            :("")
        
        } <br/>

        </>))}
         </h3>
     
     
  </>
  )
}
